mapper.select:
select inquiry_num_pk, user_id_fk, inquiry_title, inquiry_viewcount, inquiry_date, groupno, fk_seq, depthno, inquiry_fileName from ( select rownum AS rno, inquiry_num_pk, user_id_fk, inquiry_title, inquiry_viewcount, inquiry_date , groupno, fk_seq, depthno , inquiry_fileName from ( select inquiry_num_pk, user_id_fk, inquiry_title, inquiry_viewcount, to_char(inquiry_date, 'yyyy-mm-dd hh24:mi:ss') AS inquiry_date, groupno, fk_seq, depthno, inquiry_fileName from INQUIRY where inquiry_status = 1
and lower(${searchType}) like '%'|| lower(#{searchWord}) ||'%'
start with fk_seq = 0 connect by prior inquiry_num_pk = fk_seq order siblings by groupno desc, inquiry_num_pk asc ) V
) T where rno between #{startRno} and #{endRno}
